    view:allaccountdefectdetails with selectedPolicy to controlcode
}
exceptions = Dataset {
    view:allaccountexceptiondetails with selectedPolicy to controlcode
}

accountsWithExcep = Dataset {
    join:Join {
        primary-dataset:accounts
        primary-column:uid
